---
---

/*
 *    Minimal Mistakes Jekyll Theme
 *
 *  - Michael Rose
 *  - mademistakes.com
 *  - https://twitter.com/mmistakes
 *
*/

@import "vendor/breakpoint/breakpoint"; // media query mixins
@import "variables";
@import "mixins";
@import "vendor/susy/susy";

@import "reset";
@import "base";

@import "utilities";
@import "animations";
@import "tables";
@import "buttons";
@import "notices";
@import "masthead";
@import "navigation";
@import "footer";
@import "syntax";

@import "forms";

@import "page";
@import "archive";
@import "sidebar";

@import "vendor/font-awesome/fontawesome";
@import "vendor/font-awesome/solid";
@import "vendor/font-awesome/brands";
@import "vendor/magnific-popup/magnific-popup";
@import "print";

.paper-box {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 1.5em;
    padding: 2em;
    margin-bottom: 1.75em;
    background: #ffffff;
    border: 1px solid #e5e7eb;
    border-radius: 18px;
    box-shadow: 0 20px 35px -25px rgba(15, 23, 42, 0.65);
    position: relative;

    .paper-box-image{
        justify-content: center;
        display: flex;
        width: 100%;
        max-width: 460px;
        position: relative;
        margin: 0 auto;

        > div {
            width: 100%;
            border-radius: 14px;
            overflow: hidden;
            position: relative;
        }

        img {
            width: 100%;
            height: auto;
            display: block;
            display: block;
        }
    }

    .paper-box-text{
        width: 100%;
    }

    @include breakpoint($medium) {
        flex-direction: row;

        .paper-box-image{
            justify-content: flex-start;
            margin: 0;
            flex: 0 0 360px;
            max-width: 360px;
        }

        .paper-box-text{
            padding-left: 2.5em;
            flex: 1;
            max-width: 480px;
        }

    }


}

$scroll_offset : 2em;
h1:before, .anchor:before { 
    content: ''; 
    display: block; 
    position: relative; 
    width: 0; 
    height: $scroll_offset; 
    margin-top: -$scroll_offset;
}

.badge {
    padding: 0.35rem 1.1rem;
    position: absolute;
    top: 0;
    left: 2em;
    transform: translate(-4em, -55%);
    color: #ffffff;
    background: linear-gradient(180deg, #0e3a8a 0%, #0b3085 100%);
    font-size: .8em;
    font-weight: 600;
    border-radius: 6px 6px 6px 0;
    box-shadow: 0 12px 24px -18px rgba(15, 23, 42, 0.9);
    letter-spacing: 0.02em;
    z-index: 2;
}

.paper-links {
    margin-top: 1.2em;
    font-weight: 600;
    color: #2563eb;
}

.paper-links a {
    color: inherit;
    text-decoration: none;
    transition: color 0.2s ease;
}

.paper-links a:hover {
    color: #1d4ed8;
    text-decoration: underline;
}
